import React, { useRef } from 'react'
import { Link } from "react-router-dom";
import "./Header.css"
import { FaBars, FaTimes } from "react-icons/fa";

const Header = () => {

    const showSidebar = () => {
        const sidebar = document.querySelector('.sidebar')
        sidebar.style.display = "flex"
    }

    const hideSidebar = () => {
        const sidebar = document.querySelector('.sidebar')
        sidebar.style.display = "none"
    }

    return (
        <div>
            <nav>
                <ul className='sidebar'>
                    <li onClick={hideSidebar}><IoCloseSharp /></li>
                    <li><Link to="/">HOME</Link></li>
                    <li><Link to="/about">ABOUT</Link></li>
                    <li><Link to="/services">SERVICES</Link></li>
                    <li><Link to="/contact">CONTACT</Link></li>
                </ul>

                <ul>
                    <li><Link to="/">LOGO</Link></li>
                    <li className='hideOnMobile'><Link to="/">HOME</Link></li>
                    <li className='hideOnMobile'><Link to="/about">ABOUT</Link></li>
                    <li className='hideOnMobile'><Link to="/services">SERVICES</Link></li>
                    <li className='hideOnMobile'><Link to="/contact">CONTACT</Link></li>
                    <li className='menu-button' onClick={showSidebar}><GiHamburgerMenu /></li>
                </ul>
            </nav>
        </div>
    )
}

export default Header



